var password=document.getElementById("password"),viewPasswordButton=document.getElementById("viewPass"),submit=document.getElementById("submitForm");function clicked(){$("#errorHero").empty(),$("#passwordError").empty();if("123456"==password.value){var s=document.querySelector(".login__card");$(s).empty(),$(s).css("text-align","center"),$(s).append('<div class="hero-icon"><div class="hero-icon__success"><div class="hero-icon__success--tip"></div><div class="hero-icon__success--long"></div><div class="hero-icon__success--placeholder"></div><div class="hero-icon__success--fix"></div></div></div><div class="spacer spacer--25"></div><h4 class="u-text-green">Password successful!</h4><p>You\'re being redirected to the project.</p><div class="spacer spacer--25"></div><div class="progress"></div>'),setTimeout(function(){location.href=pageDest},2e3)}else 0==password.value.length?($("#passwordError").append('<p class="error-text">It seems that no password was entered. Try again.</p>'),$("#password").addClass("form--error")):($("#errorHero").prepend('<img style="width:100%;" src="../images/pass-fail.gif" />'),$("#passwordError").append('<p class="error-text">Oops, you might have forgot the password. For access, <a class="link" href="mailto:parag.ryan@gmail.com">contact me</a>.</p>'),$("#password").addClass("form--error"))}function viewPass(){$(password).is("input:password")?($(password).prop("type","text"),$(".view-password span").text("Hide Password")):($(password).prop("type","password"),$(".view-password span").text("View Password"))}password.addEventListener("keydown",function(s){13===s.keyCode&&clicked()}),submit.addEventListener("click",function(){clicked()}),viewPasswordButton.addEventListener("click",function(){viewPass()});